import {useParams} from "react-router-dom";
import Rating from "../../components/book-slider/Rating";
import { books } from"../../data/books";
const Book = () => {
    const {id} = useParams();
    const book = books.find(b=> b.id === +id); // +id equal parseInt(id)
    return (
        <div className="book">
            <div className="book-content">
                <img src={`/books/${book.image}`} alt= {book.title} className="book-content-img" />
                <div className="book-content-info">
                    <h1 className="book-titile">{book.title}</h1>
                    <div className="book-author">
                        by <span>{book.author}</span> (Author)
                    </div>
                    <Rating rating={book.rating} reviews={book.reviews} />
                    <div className="book-add-to-cart">
                        <input type="number" min="1" max="100" className="book-add-to-cart-input" />
                        <button className="book-add-to-cart-btn">
                            <i className="bi bi-cart-plus"></i>
                            Add To Cart
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Book;
