import {cartInfo} from "../../data/cart";
import "./cart.css";
import OrderSummary from "./OrderSummary";
const Cart = () => {
    const totalPrice = cartInfo.reduce((acc,cur) => acc + cur.price * cur.quantity, 0)
    return (
        <div className="cart">
            <div className="cart-title"> Your Shopping Cart</div>
            <div className="cart-wrapper">
                <div className="cart-items">
                {cartInfo.map(item => 
                    <div key={item.id}className="cart-item">
                        <img src={`/books/${item.image}`} alt={item.title} className="cart-item-img" />
                        <div className="cart-item-info">
                            <div>
                                <div className="cart-item-book-title">
                                    <b>Title: </b>{item.title}
                                </div>
                                <div className="cart-item-author">
                                    <b>Author: </b>{item.author}
                                </div>
                            </div>
                            <div>
                            <div className="cart-item-quantity">
                                <button>
                                    <i className="i bi bi-plus-lg"></i>
                                </button>
                                <b>{item.quantity}</b>
                                <button>
                                    <i className="i bi bi-dash-lg"></i>
                                </button>
                            </div>
                            <div className="cart-items-price">
                                ${item.price * item.quantity}
                            </div>
                            <i className="bi bi-trash-fill"></i>
                            </div>
                        </div>
                    </div>
                    )}
                </div>
                <OrderSummary />
            </div>

        </div>
    );
}

export default Cart;
